        item = self.listWidget.item(6)
        item.setText(_translate("MainWindow", "Plank Egzersizi ->Plank egzersizi, vücudu düz bir çizgide tutarak dirsekler ve ayak uçları üzerinde sabit bir pozisyonu koruyarak çekirdek kasları güçlendirmeye yönelik bir dayanıklılık egzersizidir."))
        item = self.listWidget.item(8)
