        item = self.listWidget.item(10)
        item.setText(_translate("MainWindow", "Mekik Egzersizi -> Mekik egzersizi, sırt üstü yatarak bacaklar kıvrılmış haldeyken üst vücudun yerden kaldırılması ve tekrar yere yavaşça indirilmesi işlemiyle karın kaslarını çalıştıran bir güçlendirme hareketidir."))
        item = self.listWidget.item(12)
