        item = self.listWidget.item(14)
        item.setText(_translate("MainWindow", "Köprü Egzersizi ->Köprü egzersizi, sırt üstü yatarak ayaklar yere basılıyken kalçaları kaldırıp indirme hareketiyle glute ve alt sırt kaslarını güçlendirir."))
        item = self.listWidget.item(16)
        item.setText(_translate("MainWindow", "Kick Back Egzersizi -> Kick back egzersizi, dört ayak üzerinde dururken bir bacağı geriye doğru düz bir şekilde kaldırıp indirme hareketiyle glute kaslarını çalıştıran bir egzersizdir."))
        item = self.listWidget.item(18)
        item.setText(_translate("MainWindow", "Ters Lunge Egzersizi ->Ters lunge egzersizi, bir bacağın geriye doğru adım atılması ve ardından çömelme hareketi yapılmasıyla bacak ve kalça kaslarını çalıştıran bir egzersizdir."))
        item = self.listWidget.item(20)
